import styled from 'styled-components';
import { SearchContainer, SearchInput } from './ContactListComponent';

const Container = styled.div`
display: flex; 
flex-direction: column; 
height: 100%;
flex: 3;
background: #f6f7f8;
`;

const ProfileHeader = styled.div`
    display: flex; 
    flex-direction: row; 
    background: #ededed;
    padding: 10px;
    align-items: center;
    gap: 10px;
`; 

const ProfileImage = styled.img`
    width: 32px;
    height: 32px; 
    border-radius: 50%;
`;

const ChatBox = styled.div`
    display: flex; 
    flex-direction: row; 
    background: #f0f0f0; 
    padding: 10px; 
    align-items: center; 
    bottom: 0;
`;

const EmojiImage = styled.img`
    width: 28px; 
    height: 28px;
    opacity: 0.3;
    cursor: pointer;
`;

const MessageContainer = styled.div`
    display: flex; 
    flex-direction: column; 
    height: 100%; 
    background: #e5ddd6;
    overflow-y: auto;
`;

const MessageDiv = styled.div`
    display: flex; 
    margin: 5px 15px; 
    justify-content: ${(props) => (props.isYours ? "flex-end" : "flex-start")};
`; 

const Message = styled.div`
    background: ${(props) => (props.isYours ? "#daf8cb" : "white")};
    max-width: 50%; 
    padding: 8px 10px; 
    fontsize: 14px; 
    background: #daf8cb;
`; 

const ConversationComponent = () => {

    return <Container>
        <ProfileHeader>
            <ProfileImage src="/profiles/anna.jpg"></ProfileImage>
            Anna K
        </ProfileHeader>
        <MessageContainer>
            <MessageDiv isYours={true}>
                <Message isYours={true}>Hi</Message>
            </MessageDiv>
            <MessageDiv isYours={false}>
                <Message isYours={false}>Hi</Message>
            </MessageDiv>
        </MessageContainer>
        <ChatBox>
            <SearchContainer>
                <EmojiImage src="/smileyFace.svg"/>
                <SearchInput placeholder="type message"></SearchInput>
            </SearchContainer>
        </ChatBox>
    </Container>

}

export default ConversationComponent;
