import styled from 'styled-components';
import { SearchContainer, SearchInput } from './ContactListComponent';

const Container = styled.div`
display: flex; 
flex-direction: column; 
height: 100%;
flex: 3;
background: #f6f7f8;
`;

const ProfileHeader = styled.div`
    display: flex; 
    flex-direction: row; 
    background: #ededed;
    padding: 10px;
    align-items: center;
    gap: 10px;
`; 

const ProfileImage = styled.img`
    width: 32px;
    height: 32px; 
    border-radius: 50%;
`;

const ChatBox = styled.div`
    display: flex; 
    flex-direction: row; 
    background: #f0f0f0; 
    padding: 10px; 
    align-items: center; 
    bottom: 0;
`;

const ConversationComponent = () => {

    return <Container>
        <ProfileHeader>
            <ProfileImage src="/profiles/anna.jpg"></ProfileImage>
            Anna K
        </ProfileHeader>
        <ChatBox>
            <SearchContainer>
                <SearchInput placeholder="type message"></SearchInput>
            </SearchContainer>
        </ChatBox>
    </Container>

}

export default ConversationComponent;
