import styled from 'styled-components';
import {contactList} from "../mockData";

//TODO: turn these const variables into regular JSX and make classes/ids in a seperate css file
const Container = styled.div`
    display: flex; 
    flex-direction: column; 
    height: 100%;
    width: 100%;
    flex: 1.6;
`;
const ProfileInfoDiv = styled.div`
    display: flex; 
    flex-direction: row; 
    background: #ededed;
    padding: 10px;
`;

const ProfileImage = styled.img`
    width: 32px; 
    height: 32px; 
    border-radius: 50%;
`;

const SearchBox = styled.div`
    display: flex;
    background: #f6f6f6;
    padding:10px;
`;

export const SearchContainer = styled.div`
    display: flex; 
    flex-direction: row;
    background-color: white;
    border-radius: 16px;
    width: 100%; 
    padding: 5px 10px;
`;

const SearchIcon = styled.img`
    width: 28px; 
    height: 28px; 
`; 

export const SearchInput = styled.input`
    width: 100%; 
    outline: none; 
    border: none; 
    font-size: 15px;
    margin-left: 10px;
`; 

const ContactItem = styled.div`
    display: flex; 
    flex-direction: row; 
    border-bottom: 1px solid #f2f2f2;
    background: white; 
    cursor: pointer; 
    padding: 15px 12px; 
    :hover{
        background: #ebebeb;
    }
`; 

const ProfileIcon = styled(ProfileImage)`
    width: 38px; 
    height: 38px;
    border-radius: 50%;
    border-radius: 50%;
    margin-left: 12px;
    margin-top: 15px;
    margin-bottom: 15px;
    object-fit: cover;
`;

const ContactInfo = styled.div`
    display: flex; 
    flex-direction: column; 
    width: 100%;
    margin: 0 12px; 
`;

const ContactName = styled.span`
    width: 100%; 
    font-size: 16px; 
    color: black;
`;

const MessageText = styled.span`
    width: 100%; 
    font-size: 14px; 
    margin-top: 3px;
    color: rgba(0,0,0,0.8);
`;

const MessageTiming = styled.span`
    font-size: 12px; 
    margin-right: 10px; 
    color: rgba(0,0,0,0.45); 
    white-space: nowrap;
`;

const ContactComponent = (props) =>{
    const {userData, setSelectedChat} = props; 
    return (
    <ContactItem onClick={() => setSelectedChat(userData)}>
         <ProfileIcon src={userData.profilePic}/>
         <ContactInfo>
            <ContactName>{userData.name}</ContactName>
            <MessageText>{userData?.lastText}</MessageText>
         </ContactInfo>
         <MessageTiming>{userData.lastTextTime}</MessageTiming>
    </ContactItem>
)}
const ContactListComponent = (props) => {
    return <Container>
        <ProfileInfoDiv>
            <ProfileImage src="/profiles/anna.jpg"/>
        </ProfileInfoDiv>
        <SearchBox>
            <SearchContainer>
                <SearchIcon src={"/search-icon.svg"}/>
                <SearchInput placeholder="search"/>
            </SearchContainer>
        </SearchBox>
        {contactList.map((userData) => (
            <ContactComponent userData={userData} setSelectedChat={props.setSelectedChat}/>
        ))}
    </Container>
}

export default ContactListComponent;
